    /**
     * 
     */
    public function testSetInfos() {
        $info = array(
            'id'          => 'skeleton',
            'title'       => 'Skeleton',
            'description' => 'Provides a basic plugin implementation. (try enabling it!)',
            'version'     => '1.1.0',
            'license'     => 'GPL',
            'author'      => 'Martijn van der Kleijn',
            'website'     => 'http://www.wolfcms.org/',
            'update_url'  => 'http://www.wolfcms.org/plugin-versions.xml',
            'require_wolf_version' => '0.5.5'
        );

        define('CMS_BACKEND', true);
        Plugin::setInfos($info);

        $this->assertArrayHasKey($info['id'], Plugin::$plugins_infos);

        $this->assertEquals($info['id'], Plugin::$plugins_infos[$info['id']]->id);
        $this->assertEquals($info['title'], Plugin::$plugins_infos[$info['id']]->title);
        $this->assertEquals($info['description'], Plugin::$plugins_infos[$info['id']]->description);
        $this->assertEquals($info['version'], Plugin::$plugins_infos[$info['id']]->version);
    }

